import React from "react";
import {Pie,} from 'react-chartjs-2';

const state = {
    labels: ['Debts/Bills', 'Personal Expenses', 'Emergency Funds'],
    datasets: [
      {
        label: 'Expense Allocations',
        backgroundColor: [
          '#0023FF',
          '#4760FF',
          '#7E90FF',
          '#AFBAFF',
          '#D9DEFF'
        ],
        hoverBackgroundColor: [
        '#501800',
        '#4B5000',
        '#175000',
        '#003350',
        '#35014F'
        ],
        data: [20,20,20,20,20]
      }
    ]
  }

export default function GraphicVisualizer() {
  let income = 1000;

  return (
    <div className="GraphicVisualizer">
        <h1>PayDay</h1>
        <h2> ${income} monthly income </h2>
        <Pie
            data={state}
            options={{
                title:{
                    display:true,
                    text:'Expense Allocations',
                    fontSize:20
                },
                legend:{
                    display:true,
                    position:'right'
                }
            }}
        />
    </div>
  );
}
