import React from "react";
import {Pie,} from 'react-chartjs-2';
import Card from '@material-ui/core/Card';
import { makeStyles } from '@material-ui/core/styles';

const state = {
    labels: ['Debts/Bills', 'Personal Expenses', 'Emergency Funds'],
    datasets: [
      {
        label: 'Expense Allocations',
        backgroundColor: [
          '#1862CC',
          '#477DCB',
          '#7AA5E3',
        ],
        hoverBackgroundColor: [
        '#5FEAC0',
        '#5FEAC0',
        '#5FEAC0',
        ],
        data: [30,50,20]
      }
    ]
  }

  const useStyles = makeStyles({
    card: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });

export default function GraphicVisualizer() {
  let income = 1000;

  return (
    <div className="GraphicVisualizer">
        <h1>PayDay</h1>
        <h2> ${income} monthly income </h2>
        <Pie

            data={state}
            options={{
                responsive:false,
                maintainAspectRatio:false,
                title:{
                    display:true,
                    text:'Expense Allocations',
                    fontSize:20
                },
                legend:{
                    display:true,
                    position:'right'
                }
            }}
        />
        <Card>
            Hello
        </Card>




    </div>
  );
}
