import React from "react";
import {Pie,} from 'react-chartjs-2';
import Card from '@material-ui/core/Card';
import { makeStyles } from '@material-ui/core/styles';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const state = {
    labels: ['Debts/Bills', 'Personal Expenses', 'Emergency Funds'],
    datasets: [
      {
        label: 'Expense Allocations',
        backgroundColor: [
          '#1862CC',
          '#477DCB',
          '#7AA5E3',
        ],
        hoverBackgroundColor: [
        '#5FEAC0',
        '#5FEAC0',
        '#5FEAC0',
        ],
        data: [30,50,20]
      }
    ]
  }

  const useStyles = makeStyles({
    card: {
      maxWidth: 340,
    },
    media: {
      height: 120,
    },
  });

export default function GraphicVisualizer() {
  let income = 1000;
  const classes = useStyles();

  return (
    <div className="GraphicVisualizer">
        <h1>PayDay</h1>
        <h2> ${income} monthly income </h2>
        <Pie

            data={state}
            options={{
                responsive:false,
                maintainAspectRatio:false,
                title:{
                    display:true,
                    text:'Expense Allocations',
                    fontSize:20
                },
                legend:{
                    display:true,
                    position:'right'
                }
            }}
        />
        <Card className = {classes.card}>
            <CardActionArea>
                <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                    Recommendations
                </Typography>
                <Typography>
                    Your average Personal expsenses are 50% higher than the average person with the same income. 
                    Try these resources to help optimize personal spending.
                </Typography>
                </CardContent>
            </CardActionArea>
            <CardActions>
                <Button size="small" color="primary">Share</Button>
                <Button size="small" color="primary">Learn More</Button>
            </CardActions>
        </Card>




    </div>
  );
}
